pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@chainlink/contracts/src/v0.8/shared/interfaces/AggregatorV3Interface.sol";
import "./StableToken.sol";

/**
 * @title VaultManager
 * @dev Gerencia Cofres (Vaults) para mintar StableToken (aUSD)
 * contra colateral (ETH nativo) de forma sobrecolateralizada.
 * Este é um MVP e omite taxas de estabilidade e liquidações.
 */
contract VaultManager is ReentrancyGuard, Ownable {
    using SafeERC20 for StableToken;

    StableToken public stable;
    AggregatorV3Interface public priceFeed; // ETH/USD

    // Mapeia o dono do cofre para seu cofre
    mapping(address => Vault) public vaults;

    struct Vault {
        uint256 ethCollateral; // em Wei (1e18)
        uint256 stableDebt;    // em aUSD (1e18)
    }

    // 150% - A posição será liquidável abaixo disso.
    uint256 public constant LIQUIDATION_THRESHOLD_BPS = 15_000;
    // 160% - Mínimo para mintar/retirar (para ter uma margem)
    uint256 public constant MIN_COLLATERAL_RATIO_BPS = 16_000;
    uint256 public constant BPS = 10_000;
    uint256 public constant PRECISION = 1e18;

    event VaultUpdated(address indexed owner, uint256 ethCollateral, uint256 stableDebt);

    constructor(address _priceFeed, address _stableAddr) {
        require(_priceFeed != address(0), "price feed required");
        priceFeed = AggregatorV3Interface(_priceFeed);
        stable = StableToken(_stableAddr);
    }

    // --- Funções Principais ---

    /**
     * @dev Deposita ETH como colateral e opcionalmente minta aUSD.
     */
    function depositAndMint(uint256 _stableToMint) external payable nonReentrant {
        Vault storage vault = vaults[msg.sender];

        if (msg.value > 0) {
            vault.ethCollateral += msg.value;
        }

        if (_stableToMint > 0) {
            vault.stableDebt += _stableToMint;

            // Verifica a saúde ANTES de mintar
            require(
                getHealthFactor(msg.sender) > BPS, 
                "Vault health too low after mint"
            );

            stable.mint(msg.sender, _stableToMint);
        }

        emit VaultUpdated(msg.sender, vault.ethCollateral, vault.stableDebt);
    }

    /**
     * @dev Paga a dívida (aUSD) e opcionalmente retira o colateral (ETH).
     * O usuário DEVE aprovar o aUSD para este contrato primeiro.
     */
    function repayAndWithdraw(uint256 _stableToRepay, uint256 _ethToWithdraw) external nonReentrant {
        Vault storage vault = vaults[msg.sender];

        if (_stableToRepay > 0) {
            require(vault.stableDebt >= _stableToRepay, "repay amount too high");
            vault.stableDebt -= _stableToRepay;

            // Transfere e queima o stable
            stable.transferFrom(msg.sender, address(this), _stableToRepay);
            stable.burn(address(this), _stableToRepay);
        }

        if (_ethToWithdraw > 0) {
            require(vault.ethCollateral >= _ethToWithdraw, "withdraw amount too high");
            vault.ethCollateral -= _ethToWithdraw;

            // Verifica a saúde ANTES de retirar
            // (Permite fechar o cofre se a dívida for 0)
            if (vault.stableDebt > 0) {
                 require(
                    getHealthFactor(msg.sender) > BPS, 
                    "Vault health too low after withdraw"
                );
            }

            (bool sent, ) = msg.sender.call{value: _ethToWithdraw}("");
            require(sent, "ETH transfer failed");
        }

        emit VaultUpdated(msg.sender, vault.ethCollateral, vault.stableDebt);
    }

    // --- Funções de Leitura (View) ---

    /**
     * @dev Retorna o fator de saúde do cofre.
     * Health Factor = (Colateral_USD * BPS) / (Dívida_USD * Limite_Liquidação)
     * Se HF > 10_000 (BPS), está seguro.
     * Se HF <= 10_000 (BPS), está sujeito a liquidação.
     */
    function getHealthFactor(address _owner) public view returns (uint256) {
        Vault memory vault = vaults[_owner];
        if (vault.stableDebt == 0) return type(uint256).max; // Seguro

        uint256 collateralValueUSD = getCollateralValueUSD(vault.ethCollateral);
        uint256 debtValueUSD = vault.stableDebt; // 1 aUSD = 1 USD (assumido)

        // (collateralValueUSD * PRECISION) / debtValueUSD = Razão de Colateralização
        // Ex: (150 * 1e18) / 100 = 1.5e18 (150%)
        uint256 collateralRatio = (collateralValueUSD * PRECISION) / debtValueUSD;

        // HF = (Razão Atual * BPS) / Razão Mínima
        // Ex: (1.5e18 * 10_000) / 15_000 = 1e18 (ou 10_000 em BPS)
        // Se a razão for 160%, HF = (1.6e18 * 10_000) / 15_000 = 10666
        return (collateralRatio * BPS) / LIQUIDATION_THRESHOLD_BPS;
    }

    /**
     * @dev Retorna o valor do colateral em USD (com 18 decimais).
     */
    function getCollateralValueUSD(uint256 _ethAmount) public view returns (uint256) {
        uint256 ethPrice = getNormalizedPrice(); // USD per ETH (1e18)
        // (ethAmount (1e18) * price (1e18)) / 1e18 = USD (1e18)
        return (_ethAmount * ethPrice) / PRECISION;
    }

    /**
     * @dev Normaliza o preço do oráculo para 1e18.
     */
    function getNormalizedPrice() public view returns (uint256) {
        (, int256 p, , , ) = priceFeed.latestRoundData();
        require(p > 0, "invalid price");
        uint8 d = priceFeed.decimals();
        uint256 up = uint256(p);
        if (d == 18) return up;
        if (d < 18) return up * (10**(18 - d));
        return up / (10**(d - 18));
    }

